import React , {useState} from "react";


export default function TextForm(props) {

    const [text , setText] = useState("Please enter a Text for UpperCase");

    const btnClick = () =>{
        console.log("function clicked");
        setText("The value is changed ... btn clicked");
    }

    return (
        <>
            <div className="container">

                <div className="mb-3">
                    <label htmlFor="exampleFormControlTextarea1" className="form-label"> {props.title} </label>
                    <textarea className="form-control" id="exampleFormControlTextarea1"  value={text} rows="3">


                    </textarea>
                </div>
                <div>
                    <input  className="mybtn" onClick={btnClick} type="submit" value={props.btn}  />
                    <button  >
                        {props.btn}
                    </button>

                </div>
            </div>


        </>
    );
}
