import React , {useState} from "react";



export default function TextForm(props) {

    const [text , setText] = useState("Please enter a Text for UpperCase");

    const Listening = (event) =>{
        console.log("Listening .. . .. .. ..");

        setText(event.target.value);


    }

    const btnClick = () =>{
        console.log("function clicked" , text);

        setText(text.toUpperCase());
    }

    return (
        <>
            <div className="container">

                <div className="mb-3">

                    <label style={ {color: `${props.colo}` }} htmlFor="exampleFormControlTextarea1" className="form-label"> {props.title} </label>
                    <textarea onChange={Listening} className="form-control" id="exampleFormControlTextarea1"  value={text} rows="3">


                    </textarea>
                </div>
                <div>

                    <button  onClick={btnClick} >
                        {props.btn}
                    </button>

                </div>
            </div>

            <div className="container my-5" >
                <p>
                    No Of Words : {text.split(" ")} and character : {text.length} 
                </p>
            </div>


        </>
    );
}
